export async function fetch_staking_history(
  offset: number,
  limit: number,
  address: string,
  sortBy: string = "CREATED_AT",
  sortOrder: string = "DESC",
) {
  const [stakingHistory, total] = await Promise.all([
    prisma.$queryRaw(
      prismaStakingSqlQueries.fetchStakingHistory(
        offset,
        limit,
        address,
        sortBy,
        sortOrder,
      ),
    ),
    prisma.$queryRaw(prismaStakingSqlQueries.fetchStakingHistoryTotal(address)),
  ]);

  const serializedResults = await Promise.all(
    (stakingHistory as any[]).map(async (record: any) => ({
      ...record,
      AMOUNT:
        typeof record.AMOUNT === "bigint"
          ? Number(record.AMOUNT)
          : record.AMOUNT,
      DURATION:
        typeof record.DURATION === "bigint"
          ? Number(record.DURATION)
          : record.DURATION,
    })),
  );

  return {
    data: serializedResults,
    totalPages: Math.ceil((total as any)[0].COUNT / limit),
  };
}
```

